import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [editChore, setEditChore] = useState(null);
  const [selectedChore, setSelectedChore] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      setChores(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  const createChore = async () => {
    if (
      !newChore.title ||
      !newChore.description ||
      !newChore.due_date ||
      !newChore.priority ||
      !newChore.status
    ) {
      setError("Please fill in all fields");
      return;
    }

    try {
      await axios.post("http://localhost:8000/api/chores/", newChore);
      fetchChores();
      setNewChore({
        title: "",
        description: "",
        due_date: "",
        priority: "",
        status: "",
      });
      setError(null);
    } catch (error) {
      console.error(error);
      if (error.response && error.response.data) {
        if (error.response.data.title) {
          setError(`Title: ${error.response.data.title}`);
        } else if (error.response.data.description) {
          setError(`Description: ${error.response.data.description}`);
        } else if (error.response.data.due_date) {
          setError(`Due Date: ${error.response.data.due_date}`);
        } else if (error.response.data.priority) {
          setError(
            `Priority: ${error.response.data.priority[0]}. Valid options are: Low, Medium, High`
          );
        } else if (error.response.data.status) {
          setError(
            `Status: ${error.response.data.status[0]}. Valid options are: Pending, In Progress, Completed`
          );
        } else {
          setError("An error occurred while creating the chore");
        }
      } else {
        setError("An error occurred while creating the chore");
      }
    }
  };

  const deleteChore = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/chores/${id}`);
      fetchChores();
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while deleting the chore");
    }
  };

  const viewChore = (chore) => {
    setSelectedChore(chore);
  };

  const closeView = () => {
    setSelectedChore(null);
  };

  const startEdit = (chore) => {
    setEditChore(chore);
  };

  const cancelEdit = () => {
    setEditChore(null);
    setError(null); // Clear the error when cancelling edit
  };

  const saveEdit = async (id) => {
    if (
      !editChore.title ||
      !editChore.description ||
      !editChore.due_date ||
      !editChore.priority ||
      !editChore.status
    ) {
      setError("Please fill in all fields");
      return;
    }

    try {
      await axios.put(`http://localhost:8000/api/chores/${id}`, editChore);
      fetchChores();
      setEditChore(null);
      setError(null);
    } catch (error) {
      console.error(error);
      if (error.response && error.response.data) {
        if (error.response.data.title) {
          setError(`Title: ${error.response.data.title}`);
        } else if (error.response.data.description) {
          setError(`Description: ${error.response.data.description}`);
        } else if (error.response.data.due_date) {
          setError(`Due Date: ${error.response.data.due_date}`);
        } else if (error.response.data.priority) {
          setError(
            `Priority: ${error.response.data.priority[0]}. Valid options are: Low, Medium, High`
          );
        } else if (error.response.data.status) {
          setError(
            `Status: ${error.response.data.status[0]}. Valid options are: Pending, In Progress, Completed`
          );
        } else {
          setError("An error occurred while updating the chore");
        }
      } else {
        setError("An error occurred while updating the chore");
      }
    }
  };

  return (
    <div>
      <h1>Chores</h1>
      <div>
        <h2>Create new chore</h2>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={newChore.title}
            onChange={(e) => setNewChore({ ...newChore, title: e.target.value })}
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={newChore.description}
            onChange={(e) =>
              setNewChore({ ...newChore, description: e.target.value })
            }
          ></textarea>
        </div>
        <div>
          <label htmlFor="due_date">Due Date:</label>
          <input
            type="date"
            id="due_date"
            value={newChore.due_date}
            onChange={(e) =>
              setNewChore({ ...newChore, due_date: e.target.value })
            }
          />
          <input
            type="time"
            id="due_time"
            value={newChore.due_time}
            onChange={(e) =>
              setNewChore({ ...newChore, due_time: e.target.value })
            }
          />
        </div>
        <div>
          <label htmlFor="priority">Priority:</label>
          <select
            id="priority"
            value={newChore.priority}
            onChange={(e) =>
              setNewChore({ ...newChore, priority: e.target.value })
            }
          >
            <option value="">-- Select Priority --</option>
            {PRIORITY_CHOICES.map((choice) => (
              <option key={choice.value} value={choice.value}>
                {choice.label}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="status">Status:</label>
          <select
            id="status"
            value={newChore.status}
            onChange={(e) =>
              setNewChore({ ...newChore, status: e.target.value })
            }
          >
            <option value="">-- Select Status --</option>
            {STATUS_CHOICES.map((choice) => (
              <option key={choice.value} value={choice.value}>
                {choice.label}
              </option>
            ))}
          </select>
        </div>
        <button onClick={createChore}>Create</button>
        {error && <p>Error: {error}</p>}
      </div>
      <div>
        <h2>Chore List</h2>
        <ul>
          {chores.map((chore) => (
            <li key={chore.id}>
              <strong>{chore.title}</strong>
              {editChore && editChore.id === chore.id ? (
                <>
                  <div>
                    <label htmlFor="edit_title">Title:</label>
                    <input
                      type="text"
                      id="edit_title"
                      value={editChore.title}
                      onChange={(e) =>
                        setEditChore({ ...editChore, title: e.target.value })
                      }
                    />
                  </div>
                  <div>
                    <label htmlFor="edit_description">Description:</label>
                    <textarea
                      id="edit_description"
                      value={editChore.description}
                      onChange={(e) =>
                        setEditChore({
                          ...editChore,
                          description: e.target.value,
                        })
                      }
                    ></textarea>
                  </div>
                  <div>
                    <label htmlFor="edit_due_date">Due Date:</label>
                    <input
                      type="date"
                      id="edit_due_date"
                      value={editChore.due_date}
                      onChange={(e) =>
                        setEditChore({
                          ...editChore,
                          due_date: e.target.value,
                        })
                      }
                    />
                    <input
                      type="time"
                      id="edit_due_time"
                      value={editChore.due_time}
                      onChange={(e) =>
                        setEditChore({
                          ...editChore,
                          due_time: e.target.value,
                        })
                      }
                    />
                  </div>
                  <div>
                    <label htmlFor="edit_priority">Priority:</label>
                    <select
                      id="edit_priority"
                      value={editChore.priority}
                      onChange={(e) =>
                        setEditChore({
                          ...editChore,
                          priority: e.target.value,
                        })
                      }
                    >
                      <option value="">-- Select Priority --</option>
                      {PRIORITY_CHOICES.map((choice) => (
                        <option key={choice.value} value={choice.value}>
                          {choice.label}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label htmlFor="edit_status">Status:</label>
                    <select
                      id="edit_status"
                      value={editChore.status}
                      onChange={(e) =>
                        setEditChore({
                          ...editChore,
                          status: e.target.value,
                        })
                      }
                    >
                      <option value="">-- Select Status --</option>
                      {STATUS_CHOICES.map((choice) => (
                        <option key={choice.value} value={choice.value}>
                          {choice.label}
                        </option>
                      ))}
                    </select>
                  </div>
                  <button onClick={() => saveEdit(editChore.id)}>Save</button>
                  <button onClick={cancelEdit}>Cancel</button>
                  {error && <p>Error: {error}</p>} {/* Display error for edit */}
                </>
              ) : (
                <>
                  <button onClick={() => viewChore(chore)}>View</button>
                  <button onClick={() => startEdit(chore)}>Edit</button>
                  <button onClick={() => deleteChore(chore.id)}>Delete</button>
                </>
              )}
              {selectedChore && selectedChore.id === chore.id && (
                <div>
                  <h3>View Chore</h3>
                  <p>
                    <strong>Title:</strong> {selectedChore.title}
                  </p>
                  <p>
                    <strong>Description:</strong> {selectedChore.description}
                  </p>
                  <p>
                    <strong>Due Date:</strong> {selectedChore.due_date}
                  </p>
                  <p>
                    <strong>Priority:</strong> {selectedChore.priority}
                  </p>
                  <p>
                    <strong>Status:</strong> {selectedChore.status}
                  </p>
                  <button onClick={closeView}>Close</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
