import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [error, setError] = useState(null);
  const [selectedChore, setSelectedChore] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      setChores(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Your existing createChore, deleteChore logic...

  const updateChore = async (id, updatedChore) => {
    try {
      await axios.put(`http://localhost:8000/api/chores/${id}`, updatedChore);
      fetchChores();
      setSelectedChore(null);
      setIsEditing(false);
    } catch (error) {
      console.error(error);
      setError("An error occurred while updating the chore");
    }
  };

  const editChore = (chore) => {
    setSelectedChore(chore);
    setIsEditing(true);
  };

  return (
    <div>
      <h1>Chores</h1>

      {/* Your existing "Create new chore" form... */}

      <div>
        <h2>Chore List</h2>
        <ul>
          {chores.map((chore) => (
            <li key={chore.id}>
              <strong>{chore.title}</strong>
              <button onClick={() => setSelectedChore(chore)}>View</button>
              <button onClick={() => editChore(chore)}>Edit</button>
              <button onClick={() => deleteChore(chore.id)}>Delete</button>
              {selectedChore?.id === chore.id && (
                <>
                  {isEditing ? (
                    <>
                      {/* Add your editing fields here */}
                      <button onClick={() => updateChore(chore.id, selectedChore)}>Save</button>
                    </>
                  ) : (
                    <>
                      {/* Add your viewing fields here */}
                      <button onClick={() => setSelectedChore(null)}>Close View</button>
                    </>
                  )}
                </>
              )}
            </li>
          ))}
        </ul>
      </div>
      {error && <p>Error: {error}</p>}
    </div>
  );
}

export default App;
