import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      const updatedChores = res.data.map(chore => ({
        ...chore,
        isEditing: false
      }));
      setChores(updatedChores);
    } catch (error) {
      console.error(error);
    }
  };

  const createChore = async () => {
    // ... Your create chore logic
  };

  const updateChore = async (id, updatedChore) => {
    try {
      await axios.put(`http://localhost:8000/api/chores/${id}`, updatedChore);
      setChores(chores.map(chore => chore.id === id ? { ...updatedChore, isEditing: false } : chore));
    } catch (error) {
      console.error(error);
      setError("An error occurred while updating the chore");
    }
  };

  const deleteChore = async (id) => {
    // ... Your delete chore logic
  };

  return (
    <div>
      <h1>Chores</h1>

      {/* Create new chore form */}
      {/* ... */}

      <div>
        <h2>Chore List</h2>
        <ul>
          {chores.map((chore) => (
            <li key={chore.id}>
              {chore.isEditing ? (
                <>
                  <input
                    type="text"
                    value={chore.title}
                    onChange={(e) => updateChore(chore.id, { ...chore, title: e.target.value })}
                  />
                  {/* You can add other fields like description, due_date, priority, status */}
                </>
              ) : (
                <strong>{chore.title}</strong>
              )}
              <button onClick={() => setChores(chores.map(c => c.id === chore.id ? { ...c, isEditing: !c.isEditing } : c))}>
                {chore.isEditing ? 'Save' : 'Edit'}
              </button>
              <button onClick={() => deleteChore(chore.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
      {error && <p>Error: {error}</p>}
    </div>
  );
}

export default App;
