import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [editingChore, setEditingChore] = useState(null);
  const [error, setError] = useState(null);
  const [selectedChore, setSelectedChore] = useState(null);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      setChores(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  const createChore = async () => {
    if (
      !newChore.title ||
      !newChore.description ||
      !newChore.due_date ||
      !newChore.priority ||
      !newChore.status
    ) {
      setError("Please fill in all fields");
      return;
    }

    try {
      await axios.post("http://localhost:8000/api/chores/", newChore);
      fetchChores();
      setNewChore({
        title: "",
        description: "",
        due_date: "",
        priority: "",
        status: "",
      });
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while creating the chore");
    }
  };

  const updateChore = async () => {
    if (
      !editingChore.title ||
      !editingChore.description ||
      !editingChore.due_date ||
      !editingChore.priority ||
      !editingChore.status
    ) {
      setError("Please fill in all fields");
      return;
    }

    try {
      await axios.put(`http://localhost:8000/api/chores/${editingChore.id}`, editingChore);
      fetchChores();
      setEditingChore(null);
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while updating the chore");
    }
  };

  const startEditChore = (chore) => {
    setEditingChore(chore);
  };

  const handleEditChange = (event) => {
    setEditingChore({ ...editingChore, [event.target.name]: event.target.value });
  };

  const deleteChore = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/chores/${id}`);
      fetchChores();
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while deleting the chore");
    }
  };

  const viewChore = (chore) => {
    setSelectedChore(chore);
  };

  const closeView = () => {
    setSelectedChore(null);
  };

  return (
    <div>
      <h1>Chores</h1>
      <div>
        {/* Your create chore form here */}

        {editingChore && (
          <div>
            <h2>Edit Chore</h2>
            <label>Title:</label>
            <input name="title" value={editingChore.title} onChange={handleEditChange} />

            {/* Add fields for description, due_date, priority, and status here similar to title */}

            <button onClick={updateChore}>Update Chore</button>
            <button onClick={() => setEditingChore(null)}>Cancel Edit</button>
          </div>
        )}

        <div>
          <h2>Chore List</h2>
          <ul>
            {chores.map((chore) => (
              <li key={chore.id}>
                <strong>{chore.title}</strong>
                <button onClick={() => viewChore(chore)}>View</button>
                <button onClick={() => startEditChore(chore)}>Edit</button>
                <button onClick={() => deleteChore(chore.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Your view chore and error display code here */}
      </div>
    </div>
  );
}

export default App;
