import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

function ChoreList() {
  const [chores, setChores] = useState([]);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/chores/');
      setChores(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteChore = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/chore/${id}/`);
      fetchChores();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Chores</h1>
      {chores.map((chore) => (
        <div key={chore.id}>
          <h2>{chore.title}</h2>
          <p>Description: {chore.description}</p>
          <p>Due date: {chore.due_date}</p>
          <p>Priority: {chore.priority}</p>
          <p>Status: {chore.status}</p>
          <Link to={`/chore/${chore.id}`}>View</Link>
          <button onClick={() => deleteChore(chore.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

function ChoreDetail({ match }) {
  const [chore, setChore] = useState(null);

  useEffect(() => {
    fetchChore(match.params.id);
  }, [match.params.id]);

  const fetchChore = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/chore/${id}/`);
      setChore(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  if (!chore) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Title: {chore.title}</h2>
      <p>Description: {chore.description}</p>
      <p>Due date: {chore.due_date}</p>
      <p>Priority: {chore.priority}</p>
      <p>Status: {chore.status}</p>
      <Link to={`/chore/${chore.id}/edit`}>Edit</Link>
      <button onClick={() => deleteChore(chore.id)}>Delete</button>
    </div>
  );
}

function ChoreCreateForm() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    due_date: '',
    priority: 'medium',
    status: 'pending',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:8000/api/chores/', formData);
      setFormData({
        title: '',
        description: '',
        due_date: '',
        priority: 'medium',
        status: 'pending',
      });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Create Chore</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={formData.title} onChange={handleChange} />
        </label>
        <label>
          Description:
          <textarea name="description" value={formData.description} onChange={handleChange}></textarea>
        </label>
        <label>
          Due Date:
          <input type="datetime-local" name="due_date" value={formData.due_date} onChange={handleChange} />
        </label>
        <label>
          Priority:
          <select name="priority" value={formData.priority} onChange={handleChange}>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </label>
        <label>
          Status:
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="pending">Pending</option>
            <option value="in_progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
        </label>
        <button type="submit">Create Chore</button>
      </form>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Chores</Link>
          <Link to="/chore/create">Create Chore</Link>
        </nav>

        <Switch>
          <Route exact path="/" component={ChoreList} />
          <Route exact path="/chore/:id" component={ChoreDetail} />
          <Route exact path="/chore/create" component={ChoreCreateForm} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
