import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [error, setError] = useState(null);
  const [editingChoreId, setEditingChoreId] = useState(null);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      setChores(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  //... Keep the rest of your code the same until the render return

  return (
    <div>
      <h1>Chores</h1>
      {/* Your Create Chore section remains the same... */}
      <div>
        <h2>Chore List</h2>
        <ul>
          {chores.map((chore) => (
            <li key={chore.id}>
              <strong>{chore.title}</strong>
              <button onClick={() => setEditingChoreId(chore.id)}>View/Edit</button>
              <button onClick={() => deleteChore(chore.id)}>Delete</button>
              {editingChoreId === chore.id && (
                <div>
                  <input
                    type="text"
                    value={chore.title}
                    onChange={(e) =>
                      setChores(
                        chores.map((ch) =>
                          ch.id === chore.id ? { ...ch, title: e.target.value } : ch
                        )
                      )
                    }
                  />
                  <button onClick={() => updateChore(chore.id, chore)}>Save</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
      {error && <p>Error: {error}</p>}
    </div>
  );
}

export default App;
