import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const PRIORITY_CHOICES = [
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
];

const STATUS_CHOICES = [
  { value: "pending", label: "Pending" },
  { value: "in_progress", label: "In Progress" },
  { value: "completed", label: "Completed" },
];

function App() {
  const [chores, setChores] = useState([]);
  const [newChore, setNewChore] = useState({
    title: "",
    description: "",
    due_date: "",
    priority: "",
    status: "",
  });
  const [error, setError] = useState(null);
  const [selectedChore, setSelectedChore] = useState(null);

  useEffect(() => {
    fetchChores();
  }, []);

  const fetchChores = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/chores/");
      setChores(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  const createChore = async () => {
    if (
      !newChore.title ||
      !newChore.description ||
      !newChore.due_date ||
      !newChore.priority ||
      !newChore.status
    ) {
      setError("Please fill in all fields");
      return;
    }

    try {
      if (selectedChore) {
        await axios.put(
          `http://localhost:8000/api/chores/${selectedChore.id}`,
          newChore
        );
      } else {
        await axios.post("http://localhost:8000/api/chores/", newChore);
      }

      fetchChores();
      setNewChore({
        title: "",
        description: "",
        due_date: "",
        priority: "",
        status: "",
      });
      setSelectedChore(null);
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while saving the chore");
    }
  };

  const deleteChore = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/chores/${id}`);
      fetchChores();
      setError(null);
    } catch (error) {
      console.error(error);
      setError("An error occurred while deleting the chore");
    }
  };

  const viewChore = (chore) => {
    setSelectedChore(chore);
  };

  const closeView = () => {
    setSelectedChore(null);
  };

  const editChore = (chore) => {
    setSelectedChore(chore);
    setNewChore({
      title: chore.title,
      description: chore.description,
      due_date: chore.due_date,
      priority: chore.priority,
      status: chore.status,
    });
  };

  return (
    <div className="container">
      <h1>Chores</h1>
      <div>
        <h2>Create new chore</h2>
        <form>
          <div>
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              value={newChore.title}
              onChange={(e) =>
                setNewChore({ ...newChore, title: e.target.value })
              }
            />
          </div>
          <div>
            <label htmlFor="description">Description:</label>
            <textarea
              id="description"
              value={newChore.description}
              onChange={(e) =>
                setNewChore({ ...newChore, description: e.target.value })
              }
            ></textarea>
          </div>
          <div>
            <label htmlFor="due_date">Due Date:</label>
            <input
              type="date"
              id="due_date"
              value={newChore.due_date}
              onChange={(e) =>
                setNewChore({ ...newChore, due_date: e.target.value })
              }
            />
          </div>
          <div>
            <label htmlFor="priority">Priority:</label>
            <select
              id="priority"
              value={newChore.priority}
              onChange={(e) =>
                setNewChore({ ...newChore, priority: e.target.value })
              }
            >
              <option value="">-- Select Priority --</option>
              {PRIORITY_CHOICES.map((choice) => (
                <option key={choice.value} value={choice.value}>
                  {choice.label}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label htmlFor="status">Status:</label>
            <select
              id="status"
              value={newChore.status}
              onChange={(e) =>
                setNewChore({ ...newChore, status: e.target.value })
              }
            >
              <option value="">-- Select Status --</option>
              {STATUS_CHOICES.map((choice) => (
                <option key={choice.value} value={choice.value}>
                  {choice.label}
                </option>
              ))}
            </select>
          </div>
          <div>
            <button type="button" onClick={createChore}>
              {selectedChore ? "Update" : "Create"}
            </button>
          </div>
        </form>
        {error && <p>Error: {error}</p>}
      </div>
      <div>
        <h2>Chore List</h2>
        <ul>
          {chores.map((chore) => (
            <li key={chore.id}>
              <div>
                <strong>{chore.title}</strong>
                <button onClick={() => viewChore(chore)}>View</button>
              </div>
              <div>
                <button onClick={() => editChore(chore)}>Edit</button>
                <button onClick={() => deleteChore(chore.id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {selectedChore && (
        <div>
          <h2>View Chore</h2>
          <p>
            <strong>Title:</strong> {selectedChore.title}
          </p>
          <p>
            <strong>Description:</strong> {selectedChore.description}
          </p>
          <p>
            <strong>Due Date:</strong> {selectedChore.due_date}
          </p>
          <p>
            <strong>Priority:</strong> {selectedChore.priority}
          </p>
          <p>
            <strong>Status:</strong> {selectedChore.status}
          </p>
          <button onClick={closeView}>Close</button>
        </div>
      )}
    </div>
  );
}

export default App;
